import axios from "axios";
export const startGetProduct = () => {
  return async (dispatch) => {
    try {
      const response = await axios.get("http://localhost:3055/api/products");
      //console.log("product details", response);
      dispatch(setProducts(response.data));
    } catch (err) {
      console.log(err);
    }
  };
};

const setProducts = (data) => {
  return {
    type: "SET_PRODUCTS",
    payload: data,
  };
};

//redux
export const startCreateProduct = (formData, token) => {
  return async (dispatch) => {
    try {
      const response = await axios.post(
        "http://localhost:3055/api/products",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`, //include the token in the Authorization header
          },
        }
      );

      //handle successfull product creation here
      console.log("product Creation", response);
      dispatch(addProducts(response.data));
    } catch (err) {
      console.log(err);
    }
  };
};
const addProducts = (product) => {
  return {
    type: "ADD_PRODUCTS",
    payload: product,
  };
};
