import { useState } from "react";
import { startCreateProduct } from "../actions/product-action";
import { useDispatch } from "react-redux";
export default function ProductForm() {
  const dispatch = useDispatch();
  const [form, setForm] = useState({
    name: "",
    mrp: "",
    discount: "",
    categoryId: "",
    imagePath: "",
  });
  const [file, setFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    //client validation
    if (!file) {
      console.log("No file Uploaded");
      return;
    }

    //constructing formData
    const formData = new FormData();
    formData.append("name", formData.name);
    formData.append("mrp", formData.mrp);
    formData.append("discount", formData.discount);
    formData.append("categoryId", formData.categoryId);
    formData.append("imagePath", formData.imagePath);

    //redux
    //Ensure user is logged in and has a valid token
    const token = localStorage.getItem("token"); // Retrive teh token from localStorage
    console.log(token);
    dispatch(startCreateProduct(form, token));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const upload = () => {
    const formData = new FormData();
    formData.append("file", file);
  };

  return (
    <div>
      <h2>Add products</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label" htmlFor="name">
            Product Name
          </label>
          <input
            type="text"
            value={form.name}
            onChange={handleChange}
            name="name"
            id="name"
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label className="form-label" htmlFor="mrp">
            MRP
          </label>
          <input
            type="text"
            value={form.mrp}
            onChange={handleChange}
            name="mrp"
            id="mrp"
            className="form-control"
          />
        </div>

        <div className="form-gruop">
          <label className="form-label" htmlFor="B2BPrice">
            B2Bprice
          </label>
          <input
            type="text"
            value={form.B2BPrice}
            onChange={handleChange}
            name="B2BPrice"
            id="B2BPrice"
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="discount">
            discount
          </label>
          <input
            type="text"
            value={form.discount}
            onChange={handleChange}
            name="discount"
            id="discount"
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="categoryId">
            categoryId
          </label>
          <input
            type="text"
            value={form.categoryId}
            onChange={handleChange}
            name="categoryId"
            id="categoryId"
            className="categoryId"
          />
        </div>
        <input type="file" onChange={handleChange} />
        <button type="button" onClick={upload}></button>
        <input type="submit" className="btn btn-primary" />
      </form>
    </div>
  );
}
