import { useState } from "react";
import { startCreateProduct } from "../actions/product-action";
import { useDispatch } from "react-redux";
//import axios from "axios";
export default function ProductForm() {
  const dispatch = useDispatch();
  const [form, setForm] = useState({
    name: "",
    mrp: "",
    B2BPrice: "",
    discount: "",
    categoryId: "",
    imagePath: "",
  });
  const [file, setFile] = useState(null);
  const handleUpload = (e) => {
    console.log(file);
    const formData = new FormData();
    formData.append("name", form.name);
    formData.append("mrp", form.mrp);
    formData.append("discount", form.discount);
    formData.append("categoryId", form.categoryId);
    formData.append("file", file);

    axios
      .post("http://localhost:3055/upload", formData)
      .then((res) => {})
      .catch((err) => console.log(err));
    console.log("formData", formData);
  };
  /*const formData = new FormData();

  const upload = () => {
    //constructing formData
    formData.append("name", form.name);
    formData.append("mrp", form.mrp);
    formData.append("discount", form.discount);
    formData.append("categoryId", form.categoryId);
    formData.append("imagePath", file);

    axios
      .post("http://localhost:3055/upload", formData)
      .then((res) => {})
      .catch((err) => console.log(err));
    console.log("formData", formData);
  };
  */

  const handleSubmit = (e) => {
    e.preventDefault();

    //client validation
    /*if (!file) {
      console.log("No file Uploaded");
      return;
    }
    upload();*/

    //redux
    //Ensure user is logged in and has a valid token
    const token = localStorage.getItem("token"); // Retrive teh token from localStorage
    console.log(token);
    dispatch(startCreateProduct(form, token));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    //calculate B2B price
    if (name === "mrp" || name === "discount") {
      //parse values to numbers
      const mrp = parseFloat(form.mrp);
      console.log("mrp", typeof mrp);
      const discount = parseFloat(form.discount);
      console.log("discount", discount);

      const B2BPrice = Math.abs(mrp * (1 - discount / 100));
      setForm({ ...form, [name]: value, B2BPrice });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  return (
    <div>
      <h2>Add products</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label" htmlFor="name">
            Product Name
          </label>
          <input
            type="text"
            value={form.name}
            onChange={handleChange}
            name="name"
            id="name"
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label className="form-label" htmlFor="mrp">
            MRP
          </label>
          <input
            type="text"
            value={form.mrp}
            onChange={handleChange}
            name="mrp"
            id="mrp"
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="discount">
            discount
          </label>
          <input
            type="text"
            value={form.discount}
            onChange={handleChange}
            name="discount"
            id="discount"
            className="form-control"
          />
        </div>
        <div className="form-gruop">
          <label className="form-label" htmlFor="B2BPrice">
            B2Bprice
          </label>
          <input
            type="text"
            value={form.B2BPrice}
            onChange={handleChange}
            name="B2BPrice"
            id="B2BPrice"
            className="form-control"
            disabled
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="categoryId">
            categoryId
          </label>
          <input
            type="text"
            value={form.categoryId}
            onChange={handleChange}
            name="categoryId"
            id="categoryId"
            className="categoryId"
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="Image">
            Image
          </label>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            className="form-control"
          />
          <button type="button" onClick={handleUpload}>
            Upload
          </button>
        </div>
        <input type="submit" className="btn btn-primary" />
      </form>
    </div>
  );
}
