//import React, { useState } from "react";
import { useState } from "react";
import "./LoginPage.css";
import { LoginApi } from "../services/Api";
//import { storeUserData } from "../services/Storage";
//import { isAuthenticated } from "../services/Auth";
import { useDispatch } from "react-redux";
import { login } from "../actions/authAction";
import { Link, Navigate, useNavigate } from "react-router-dom";

export default function LoginPage() {
  const dispatch = useDispatch();
  const [credential, setCredential] = useState({
    username: "",
    password: "",
  });

  const navigate = useNavigate();
  const initialStateErrors = {
    email: { required: false },
    password: { required: false },
    role: { required: false },
    custom_errors: null,
  };
  const [inputs, setInputs] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState(initialStateErrors);
  const [loading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let updateErrors = { ...errors };
    let hasError = false;
    //validating form field
    if (inputs.email === "") {
      updateErrors.email.required = true;
      hasError = true;
    }
    if (inputs.password === "") {
      updateErrors.password.required = true;
      hasError = false;
    }
    if (!hasError) {
      setIsLoading(true);

      //seding Login api request
      LoginApi(inputs)
        .then((response) => {
          storeUserData(response.data.idToken);
        })
        .catch((err) => {
          console.log(err);
          if ((err.code = "ERR_BAD_REQUEST")) {
            setErrors({ ...errors, custom_errors: "Invalid credentials" });
          }
          //setErrors(err.response.data.errors);
          //setErrors(err.errors.msg);
        })
        .finally(() => {
          setIsLoading(false);
        });

      //localstoage
      const loggedUser = JSON.parse(localStorage.getItem("user"));
      if (
        inputs.email === loggedUser.email &&
        inputs.password === loggedUser.password
      ) {
        localStorage.setItem("loggedIn", true);
        navigate("/");
      } else {
        alert("wrong email or ppassword");
      }
    }
    setErrors({ ...errors });
  };

  const handleInputs = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };
  //if isauthenticated is true navigate to dashboard
  if (isAuthenticated()) {
    return <Navigate to="/retailerContainer" />;
  }
  return (
    <div>
      <section className="login-block">
        <div className="container">
          <div className="row ">
            <div className="col login-sec">
              <h2 className="text-center">Login Now</h2>
              <form onSubmit={handleSubmit} className="login-form" action="">
                <div className="form-group">
                  <label
                    htmlFor="exampleInputEmail1"
                    className="text-uppercase"
                  >
                    Email
                  </label>
                  <input
                    type="email"
                    onChange={handleInputs}
                    className="form-control"
                    name="email"
                    id=""
                    placeholder="email"
                  />
                  {errors.email.required ? (
                    <span className="text-danger">Email is required.</span>
                  ) : null}
                </div>
                <div className="form-group">
                  <label
                    htmlFor="exampleInputPassword1"
                    className="text-uppercase"
                  >
                    Password
                  </label>
                  <input
                    className="form-control"
                    onChange={handleInputs}
                    type="password"
                    name="password"
                    placeholder="password"
                    id=""
                  />
                  {errors.password.required ? (
                    <span className="text-danger">Password is required.</span>
                  ) : null}
                </div>

                <div className="form-group">
                  <div className="text-center">
                    {loading ? (
                      <div className="text-center">
                        <div
                          className="spinner-border text-primary "
                          role="status"
                        >
                          <span className="sr-only">Loading...</span>
                        </div>
                      </div>
                    ) : null}
                  </div>

                  <span className="text-danger">
                    {errors.custom_errors ? (
                      <p>{errors.custom_errors}</p>
                    ) : null}
                  </span>
                  <input
                    type="submit"
                    className="btn btn-login float-right"
                    disabled={loading}
                    value="Login"
                  />
                </div>
                <div className="clearfix"></div>
                <div className="form-group">
                  Create new account ? Please{" "}
                  <Link to="/register">Register</Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
