import { useState } from "react";
import "./RegisterPage.css";
import bcrypt from "bcryptjs";
import axios from "axios";
import { isAuthenticated } from "../services/Auth";
//import { storeUserData } from "../services/Storage";
//import { RegisterApi } from "../services/Api";
import { Navigate } from "react-router-dom";
export default function Registerpage() {
  const initialStateErrors = {
    username: { required: false },
    email: { required: false },
    password: { required: false },
    role: { required: false },
    custom_error: null,
  };
  const [errors, setErrors] = useState(initialStateErrors);
  const [loading, setLoading] = useState(false);
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
    password: "",
    role: "",
  });
  const [serverErrors, setServerErrors] = useState([]);
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [hideForm, setHideForm] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let updateErrors = { ...errors };
    let hasError = false;

    //validating form field
    if (inputs.username === "") {
      updateErrors.username.required = true;
      hasError = true;
    }
    if (inputs.email === "") {
      updateErrors.email.required = true;
      hasError = true;
    }

    if (inputs.password === "") {
      updateErrors.password.required = true;
      hasError = true;
    }
    if (inputs.role === "") {
      updateErrors.role.required = true;
      hasError = true;
    }
    console.log(inputs);
    if (!hasError) {
      setLoading(true);

      //hash the password
      const hashedPassword = await bcrypt.hash(inputs.password, 10);

      //set createdAt field before sending data to the server
      const dataToSend = {
        ...inputs,
        password: hashedPassword,
        createdAt: new Date().toISOString(), //set current date and time
      };

      //sending register API request
      try {
        const response = await axios.post(
          "http://localhost:3055/api/user/register",
          dataToSend
        );
        //storeUserData(response.data.idToken);
        console.log("Registeration successful", response.data);
        setRegistrationSuccess(true);
        setHideForm(true);
      } catch (err) {
        setServerErrors(err.response.data.errors);
        console.log("Registration falied", err);
      } finally {
        setLoading(false);
      }
    } else {
      setErrors(updateErrors);
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });

    //when user start typing error message should be cleared
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: { required: false },
    }));
  };

  if (isAuthenticated) {
    //redirect user to dashboard
    return <Navigate to="/dashboard" />;
  }

  return (
    <section className="register-block">
      <div className="container">
        <div className="row ">
          <div className="col register-sec">
            <h2 className="text-center">Welcome to Retalio</h2>
            <p>
              Retalio is a platform where both retailers and customers come
              together to buy or sell and stay connected to fulfil their needs
            </p>
            {registrationSuccess && serverErrors.length === 0 && (
              <div className="alert alert-success">
                User registered successfully!
                <div>
                  <a href="#;" className="btn btn-primary">
                    Login
                  </a>
                </div>
              </div>
            )}

            {!hideForm && ( // Render the form only if hideForm is false
              <form onSubmit={handleSubmit} className="register-form" action="">
                <div className="form-group">
                  <label
                    htmlFor="exampleInputEmail1"
                    className="text-uppercase"
                  >
                    username
                  </label>

                  <input
                    type="text"
                    onChange={handleInput}
                    className="form-control"
                    name="username"
                    id=""
                  />
                  {errors.username.required ? (
                    <span className="text-danger">Name is required.</span>
                  ) : null}
                </div>
                <div className="form-group">
                  <label
                    htmlFor="exampleInputEmail1"
                    className="text-uppercase"
                  >
                    Email
                  </label>
                  <input
                    type="text"
                    onChange={handleInput}
                    className="form-control"
                    name="email"
                    id=""
                  />
                  {errors.email.required ? (
                    <span className="text-danger">Email is required.</span>
                  ) : null}
                </div>

                <div className="form-group">
                  <label
                    htmlFor="exampleInputPassword1"
                    className="text-uppercase"
                  >
                    Password
                  </label>
                  <input
                    className="form-control"
                    type="password"
                    onChange={handleInput}
                    name="password"
                    id=""
                  />
                  {errors.password.required ? (
                    <span className="text-danger">Password is required.</span>
                  ) : null}
                </div>
                <div>
                  <span>Please select a role</span>
                  <div>
                    <label>
                      <input
                        type="radio"
                        name="role"
                        value="admin"
                        onChange={handleInput}
                      />
                      Admin
                    </label>
                    <label style={{ marginLeft: "10px" }}>
                      <input
                        type="radio"
                        name="role"
                        value="employee"
                        onChange={handleInput}
                      />
                      Employee
                    </label>
                    <label style={{ marginLeft: "10px" }}>
                      <input
                        type="radio"
                        name="role"
                        value="retailer"
                        onChange={handleInput}
                      />
                      Retailer
                    </label>
                  </div>
                </div>
                <div className="form-group">
                  <span className="text-danger"></span>
                  {loading ? (
                    <div className="text-center">
                      <div
                        className="spinner-border text-primary "
                        role="status"
                      >
                        <span className="sr-only">Loading...</span>
                      </div>
                    </div>
                  ) : (
                    <input
                      type="submit"
                      className="btn btn-login float-right"
                      disabled={loading}
                      value="Register"
                    />
                  )}
                </div>
                <div className="clearfix"></div>
                <div className="form-group">
                  <a href="#;">Login</a>
                </div>
              </form>
            )}
            {serverErrors.length > 0 && (
              <div>
                <ul>
                  {serverErrors.map((ele, i) => {
                    return (
                      <li key={i} className="text-danger">
                        {ele.msg}
                      </li>
                    );
                  })}
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </section>
  );
}
