const initialState = {
  data: [],
};
console.log("InitialState", initialState);
const productsReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_PRODUCTS": {
      return { ...state, data: action.payload };
    }
    case "ADD_PRODUCTS": {
      return { ...state, data: [...state.data, action.payload] };
    }
    default: {
      return { ...state };
    }
  }
};

export default productsReducer;
