import { useState } from "react";
import "./RegisterPage.css";
export default function Registerpage() {
  const initialStateErrors = {
    name: { required: false },
    email: { required: false },
    mobileNumber: { required: false },
    password: { required: false },
    role: { required: false },
    custom_error: null,
  };
  const [errors, setErrors] = useState(initialStateErrors);
  const [loading, setLoading] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    let updateErrors = { ...errors };
    let hasError = false;
    if (inputs.name === "") {
      updateErrors.name.required = true;
      hasError = true;
    }
    if (inputs.email === "") {
      updateErrors.email.required = true;
      hasError = true;
    }
    if (inputs.mobileNumber === "") {
      updateErrors.mobileNumber.required = true;
      hasError = true;
    }
    if (inputs.password === "") {
      updateErrors.password.required = true;
      hasError = true;
    }
    if (!hasError) {
      setLoading(true);
    }
    if (errors === true) {
      setErrors(true);
    }
    setErrors(updateErrors);
  };
  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    mobileNumber: "",
    password: "",
    role: "",
  });

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });

    //when user start typing error message should be cleared
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: { required: false },
    }));
  };

  return (
    <section className="register-block">
      <div className="container">
        <div className="row ">
          <div className="col register-sec">
            <h2 className="text-center">Welcome to Retalio</h2>
            <p>
              Retalio is a platform where both retailers and customers come
              together to buy or sell and stay connected to fulfil their needs
            </p>
            <form onSubmit={handleSubmit} className="register-form" action="">
              <div className="form-group">
                <label htmlFor="exampleInputEmail1" className="text-uppercase">
                  Name
                </label>

                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="name"
                  id=""
                />
                {errors.name.required ? (
                  <span className="text-danger">Name is required.</span>
                ) : null}
              </div>
              <div className="form-group">
                <label htmlFor="exampleInputEmail1" className="text-uppercase">
                  Email
                </label>
                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="email"
                  id=""
                />
                {errors.email.required ? (
                  <span className="text-danger">Email is required.</span>
                ) : null}
              </div>
              <div className="form-group">
                <label
                  htmlFor="exampleInputPassword1"
                  className="text-uppercase"
                >
                  Mobile NUMBER
                </label>
                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="mobileNumber"
                  id=""
                />
                {errors.mobileNumber.required ? (
                  <span className="text-danger">MobileNo is required.</span>
                ) : null}
              </div>
              <div className="form-group">
                <label
                  htmlFor="exampleInputPassword1"
                  className="text-uppercase"
                >
                  Password
                </label>
                <input
                  className="form-control"
                  type="password"
                  onChange={handleInput}
                  name="password"
                  id=""
                />
                {errors.password.required ? (
                  <span className="text-danger">Password is required.</span>
                ) : null}
              </div>
              <div>
                <span>Please select a role</span>
                <div>
                  <label>
                    <input type="radio" name="role" value="admin" />
                    Admin
                  </label>
                  <label style={{ marginLeft: "10px" }}>
                    <input type="radio" name="role" value="employee" />
                    Employee
                  </label>
                  <label style={{ marginLeft: "10px" }}>
                    <input type="radio" name="role" value="retailer" />
                    Retailer
                  </label>
                </div>
              </div>
              <div className="form-group">
                <span className="text-danger">
                  {errors.custom_error ? <p>Custom Error Message!</p> : null}
                </span>
                {loading ? (
                  <div className="text-center">
                    <div className="spinner-border text-primary " role="status">
                      <span className="sr-only">Loading...</span>
                    </div>
                  </div>
                ) : null}

                <input
                  type="submit"
                  className="btn btn-login float-right"
                  value="Register"
                />
              </div>
              <div className="clearfix"></div>
              <div className="form-group">
                <a href="#;">Login</a>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
