import { useState } from "react";
import "./RegisterPage.css";
import axios from "axios";
//import { RegisterApi } from "../services/Api";
export default function Registerpage() {
  const initialStateErrors = {
    username: { required: false },
    email: { required: false },
    //mobileNo: { required: false },
    password: { required: false },
    role: { required: false },
    custom_error: null,
  };
  const [errors, setErrors] = useState(initialStateErrors);
  const [loading, setLoading] = useState(false);
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
    //mobileNo: "",
    password: "",
    role: "",
  });
  const [serverErrors, setServerErrors] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let updateErrors = { ...errors };
    let hasError = false;

    //validating form field
    if (inputs.username === "") {
      updateErrors.username.required = true;
      hasError = true;
    }
    if (inputs.email === "") {
      updateErrors.email.required = true;
      hasError = true;
    }
    /*if (inputs.mobileNo === "") {
      updateErrors.mobileNo.required = true;
      hasError = true;*/

    if (inputs.password === "") {
      updateErrors.password.required = true;
      hasError = true;
    }
    if (inputs.role === "") {
      updateErrors.role.required = true;
      hasError = true;
    }
    console.log(inputs);
    if (!hasError) {
      setLoading(true);

      //set createdAt field before sending data to the server
      const dataToSend = {
        ...inputs,
        createdAt: new Date().toISOString(), //set current date and time
      };

      //sending register API request
      try {
        const response = await axios.post(
          "http://localhost:3055/api/user/register",
          dataToSend
        );
        console.log("Registeration successful", response.data);
      } catch (err) {
        setServerErrors(err.errors);
        console.log("Registration falied", err.errors);
      } finally {
        setLoading(false);
      }
    } else {
      setErrors(updateErrors);
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });

    //when user start typing error message should be cleared
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: { required: false },
    }));
  };

  return (
    <section className="register-block">
      <div className="container">
        <div className="row ">
          <div className="col register-sec">
            <h2 className="text-center">Welcome to Retalio</h2>
            <p>
              Retalio is a platform where both retailers and customers come
              together to buy or sell and stay connected to fulfil their needs
            </p>
            {/*serverErrors.length > 0 && (
              <div>
                <h2>server Errors</h2>
                <ul>
                  {serverErrors.map((ele, i) => {
                    return <li key={i}>{ele.msg}</li>;
                  })}
                </ul>
              </div>
            )*/}
            <form onSubmit={handleSubmit} className="register-form" action="">
              <div className="form-group">
                <label htmlFor="exampleInputEmail1" className="text-uppercase">
                  username
                </label>

                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="username"
                  id=""
                />
                {errors.username.required ? (
                  <span className="text-danger">Name is required.</span>
                ) : null}
              </div>
              <div className="form-group">
                <label htmlFor="exampleInputEmail1" className="text-uppercase">
                  Email
                </label>
                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="email"
                  id=""
                />
                {errors.email.required ? (
                  <span className="text-danger">Email is required.</span>
                ) : null}
              </div>
              {/*<div className="form-group">
                <label
                  htmlFor="exampleInputPassword1"
                  className="text-uppercase"
                >
                  Mobile NUMBER
                </label>
                <input
                  type="text"
                  onChange={handleInput}
                  className="form-control"
                  name="mobileNo"
                  id=""
                />
                {errors.mobileNo.required ? (
                  <span className="text-danger">MobileNo is required.</span>
                ) : null}
              </div> */}
              <div className="form-group">
                <label
                  htmlFor="exampleInputPassword1"
                  className="text-uppercase"
                >
                  Password
                </label>
                <input
                  className="form-control"
                  type="password"
                  onChange={handleInput}
                  name="password"
                  id=""
                />
                {errors.password.required ? (
                  <span className="text-danger">Password is required.</span>
                ) : null}
              </div>
              <div>
                <span>Please select a role</span>
                <div>
                  <label>
                    <input
                      type="radio"
                      name="role"
                      value="admin"
                      onChange={handleInput}
                    />
                    Admin
                  </label>
                  <label style={{ marginLeft: "10px" }}>
                    <input
                      type="radio"
                      name="role"
                      value="employee"
                      onChange={handleInput}
                    />
                    Employee
                  </label>
                  <label style={{ marginLeft: "10px" }}>
                    <input
                      type="radio"
                      name="role"
                      value="retailer"
                      onChange={handleInput}
                    />
                    Retailer
                  </label>
                </div>
              </div>
              <div className="form-group">
                <span className="text-danger">
                  {errors.custom_error ? <p>Custom Error Message!</p> : null}
                </span>
                {loading ? (
                  <div className="text-center">
                    <div className="spinner-border text-primary " role="status">
                      <span className="sr-only">Loading...</span>
                    </div>
                  </div>
                ) : null}

                <input
                  type="submit"
                  className="btn btn-login float-right"
                  disabled={loading}
                  value="Register"
                />
              </div>
              <div className="clearfix"></div>
              <div className="form-group">
                <a href="#;">Login</a>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
